import * as React from "react";
import Button from "@mui/material/Button";
import ClickAwayListener from "@mui/material/ClickAwayListener";
import Grow from "@mui/material/Grow";
import Paper from "@mui/material/Paper";
import Popper from "@mui/material/Popper";
import MenuItem from "@mui/material/MenuItem";
import { useTranslation } from "react-i18next";
import MenuList from "@mui/material/MenuList";
import Stack from "@mui/material/Stack";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import RomFlag from "../../assets/icons/RomFlag.png";
import RussianFlag from "../../assets/icons/RussianFlag.png";
import EnglishFlag from "../../assets/icons/EnglishFlag.png";
import { Box } from "@mui/system";

export default function LanguageButton({ smallSize }) {
  const [open, setOpen] = React.useState(false);
  const anchorRef = React.useRef(null);

  const handleToggle = () => {
    setOpen((prevOpen) => !prevOpen);
  };

  const handleClose = (event) => {
    console.log(anchorRef.current, "current");
    console.log(event.target, "event");

    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return;
    }

    setOpen(false);
  };

  function handleListKeyDown(event) {
    if (event.key === "Tab") {
      event.preventDefault();
      setOpen(false);
    } else if (event.key === "Escape") {
      setOpen(false);
    }
  }

  // return focus to the button when we transitioned from !open -> open
  const prevOpen = React.useRef(open);
  const { i18n } = useTranslation();

  const handleLanguage = (lang) => {
    i18n.changeLanguage(lang);
  };

  React.useEffect(() => {
    if (prevOpen.current === true && open === false) {
      anchorRef.current.focus();
    }

    prevOpen.current = open;
  }, [open]);

  function RomFlagLang() {
    return <img src={RomFlag} alt="" />;
  }

  function RussianFlagLang() {
    return <img src={RussianFlag} alt="" />;
  }

  function AmericanFlagLang() {
    return <img src={EnglishFlag} alt="" />;
  }

  return (
    <Stack direction="row" spacing={2}>
      <div>
        <Button
          size={smallSize ? smallSize : ""}
          ref={anchorRef}
          sx={{ color: "black" }}
          aria-controls={open ? "composition-menu" : undefined}
          aria-expanded={open ? "true" : undefined}
          aria-haspopup="true"
          onClick={handleToggle}
          endIcon={<ExpandMoreIcon />}
        >
          {localStorage.getItem("i18nextLng") === "ro" && RomFlagLang()}
          {localStorage.getItem("i18nextLng") === "ra" && RussianFlagLang()}
          {localStorage.getItem("i18nextLng") === "en" && AmericanFlagLang()}
          {localStorage.getItem("i18nextLng") === "en-US" && AmericanFlagLang()}

          <Box ml={1}>
            {localStorage.getItem("i18nextLng") === "ro" && "Room"}
            {localStorage.getItem("i18nextLng") === "ra" && "Russian"}
            {localStorage.getItem("i18nextLng") === "en" && "English"}
            {localStorage.getItem("i18nextLng") === "en-US" && "English"}
          </Box>
        </Button>
        <Popper
          sx={{ zIndex: 9999 }}
          open={open}
          anchorEl={anchorRef.current}
          role={undefined}
          placement="bottom-start"
          transition
          disablePortal
        >
          {({ TransitionProps, placement }) => (
            <Grow
              sx={{ zIndex: 9999 }}
              {...TransitionProps}
              style={{
                transformOrigin:
                  placement === "bottom-start" ? "left top" : "left bottom",
              }}
            >
              <Paper sx={{ zIndex: 9999 }}>
                <ClickAwayListener onClickAway={handleClose}>
                  <MenuList
                    sx={{ zIndex: 9999, mt: 0.1 }}
                    autoFocusItem={open}
                    id="composition-menu"
                    onKeyDown={handleListKeyDown}
                  >
                    <MenuItem>
                      <Button
                        size={smallSize ? smallSize : ""}
                        sx={{ color: "black", my: -0.7, mx: -1.6 }}
                        startIcon={RomFlagLang()}
                        onClick={(e) => {
                          handleClose(e);
                          handleLanguage("ro");
                        }}
                      >
                        Room
                      </Button>
                    </MenuItem>
                    <MenuItem>
                      <Button
                        size={smallSize ? smallSize : ""}
                        sx={{ color: "black", my: -0.7, mx: -1.6 }}
                        startIcon={RussianFlagLang()}
                        onClick={(e) => {
                          handleClose(e);
                          handleLanguage("ra");
                        }}
                      >
                        Russian
                      </Button>
                    </MenuItem>
                    <MenuItem>
                      <Button
                        size={smallSize ? smallSize : ""}
                        sx={{ color: "black", my: -0.7, mx: -1.6 }}
                        startIcon={AmericanFlagLang()}
                        onClick={(e) => {
                          handleClose(e);
                          handleLanguage("en");
                        }}
                      >
                        English
                      </Button>
                    </MenuItem>
                  </MenuList>
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </div>
    </Stack>
  );
}
