import React, { useState } from "react";
import {
  AppBar,
  Toolbar,
  Grid,
  Box,
  Typography,
  Paper,
  Button,
} from "@mui/material";
import { makeStyles } from "@mui/styles";
import Link from "@mui/material/Link";
import Avatar from "@mui/material/Avatar";
import Logo from "../../assets/Logo.png"
import Hidden from "@mui/material/Hidden";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import SwipeableDrawer from "@mui/material/SwipeableDrawer";
import Divider from "@mui/material/Divider";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import { useNavigate, useLocation } from "react-router-dom";
// import { useTranslation } from "react-i18next";
// import LanguageButton from "../Utils/LanguageButton";

const navigationLinks = [
  { name: "home", to: "/" },
  { name: "Blog", to: "/blog" },
  { name: "About Us", to: "/about-us" },
  { name: "Contact", to: "/contact-us" },
];

const useStyles = makeStyles(() => ({
  navBg: {
    backgroundColor: "#fff !important",
    color: "white",
    border: "none",
    boxShadow: "0px 0px 0px 0px",
    transition: "all 900ms ease-in-out !important",
    zIndex: 100,
  },
  checkAvt: {
    width: "100%",
    height: "auto",
    borderRadius: 0,
  },
  parentActive: {
    position: "relative",
  },
  childActive: {
    position: "absolute",
    backgroundColor: "red",
    borderRadius: "3px",
    width: "20px",
    height: "3px",
    bottom: -15,
  },
  active: {
    borderWidth: "20px",
    textAlign: "left",
    fontSize: "20px",
    letterSpacing: "0px",
    color: "#8A8A8A",
    marginRight: "40px",
    cursor: "pointer",
    fontWeight: "normal",
    textTransform: "capitalize",
    fontFamily: "Poppins",
    "&:hover": { color: "#000" },
    "&:lastChild": { marginRight: "0" },
    "@media (max-width: 899.9px)": {
      color: "#000",
      fontWeight: "bold",
      fontSize: "15px",
      borderBottom: "2px solid #000",
      "&:hover": { color: "black" },
    },
    "@media (min-width: 1899.9px)": {
      marginRight: "58px",
    },
    "@media (min-width: 1200px) and (max-width: 1599.9px)": {
      fontSize: "16px",
      marginRight: "28px",
    },
    "@media (min-width: 900px) and (max-width: 1199.9px)": {
      fontSize: "12px",
      marginRight: "20px",
    },
  },
  nonActive: {
    textAlign: "left",
    fontSize: "20px",
    letterSpacing: "0px",
    color: "#8A8A8A",
    marginRight: "40px",
    cursor: "pointer",
    fontWeight: "normal",
    fontFamily: "Poppins",
    textTransform: "capitalize",
    "&:lastChild": { marginRight: "0" },
    "@media (max-width: 899.9px)": {
      color: "#000",
      fontSize: "15px",
      fontWeight: "bold",
    },
    "@media (min-width: 1899.9px)": {
      marginRight: "58px",
    },
    "@media (min-width: 1200px) and (max-width: 1599.9px)": {
      fontSize: "16px",
      marginRight: "28px",
    },
    "@media (min-width: 900px) and (max-width: 1199.9px)": {
      fontSize: "14px",
      marginRight: "12px",
    },
  },
}));

function Header() {
  const classes = useStyles();
  let navigate = useNavigate();
  let location = useLocation();

  const [open, setOpen] = useState(false);

 

  return (
    <AppBar className={classes.navBg} sx={{ padding: "1% 0 !important" }}>
      <Toolbar sx={{ padding: "0 5% !important" }}>
        <Grid container sx={{ justifyContent: "space-between" }}>
          <Grid
            item
            md={2.5}
            sm={6}
            xs={6}
            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "start",
            }}
          >
            <Box
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "start",
                px: { lg: 1, md: 1, sm: 2, xs: 2 },
              }}
            >
              <Link to="/">
                <Avatar className={classes.checkAvt} src={Logo} />
              </Link>
            </Box>
          </Grid>
          <Hidden mdDown>
            <Grid
              item
              md={6}
              sm={5}
              xs={6}
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: {
                  xs: "end",
                  md: "end",
                },
                pl: { xs: "7%" },
              }}
            >
              {navigationLinks.map((item, index) => {
                return (
                  <Box
                    key={index}
                    sx={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    <Link
                      className={classes.nonActive}
                      onClick={() => {
                        navigate(item.to);
                      }}
                      variant="button"
                      underline="none"
                    >
                      <Box
                        className={classes.parentActive}
                        display={"flex"}
                        justifyContent={"center"}
                      >
                        <Box
                          className={
                            location?.pathname === item.to
                              ? classes.childActive
                              : " "
                          }
                        />
                        {item.name}
                      </Box>
                    </Link>
                  </Box>
                );
              })}
            
            </Grid>
          </Hidden>

          {/* Bar Icon */}
          <Hidden mdUp>
            <Grid
              item
              xs={6}
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "end",
              }}
            >
              
              <IconButton
                onClick={() => setOpen(true)}
                sx={{ paddingRight: 0, paddingLeft: "20px" }}
              >
                <MenuIcon
                  style={{ color: "#000", width: "1.2em", height: "2em" }}
                />
              </IconButton>
            </Grid>
          </Hidden>
        </Grid>
      </Toolbar>

      {/* Navbar for small screens */}
      <SwipeableDrawer
        anchor="right"
        open={open}
        onOpen={() => setOpen(true)}
        onClose={() => setOpen(false)}
      >
        <div
          onClick={() => setOpen(false)}
          onKeyPress={() => setOpen(false)}
          role="button"
          tabIndex={0}
        >
          <IconButton sx={{ pt: 2, pb: 2, ml: -1 }}>
            <ChevronRightIcon sx={{ width: "2em" }} />
          </IconButton>
        </div>
        <Divider />
        <List>
          {navigationLinks.map((item, index) => (
            <ListItem key={index}>
              <Link
                className={
                  location?.pathname === item.to
                    ? classes.active
                    : classes.nonActive
                }
                onClick={() => {
                  navigate(item.to);
                }}
                variant="button"
                underline="none"
              >
                <Typography variant="h7">{item.name}</Typography>
              </Link>
            </ListItem>
          ))}
        </List>
      </SwipeableDrawer>
    </AppBar>
  );
}

export default Header;
