import React from "react";
import Image from "../../assets/Image.png";
import { Box, Typography, Button } from "@mui/material";
import { makeStyles } from "@mui/styles";
// import rightHalf from "../../assets/images/right-2.png";


const useStyle = makeStyles({
  btn: {
    marginTop: "3% !important",
    "@media(min-width: 1390px)": {
      marginTop: "1% !important",
    },
  },
  box: {
    width: "50%",
   
    backgroundImage: `url(${Image})`,
    backgroundSize: "cover !important",
    backgroundPosition: "center",
  },

  article: {
    height: '100%',
    position: 'relative',
    overflow: 'hidden',
},

picture: {
    objectFit: 'cover',
    width: '100%',
    height: '100%',
},

header: {
    fontSize: '80px',
    color: 'white',
    textAlign: 'center',
    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    height: fit-content,
    margin: auto,
}
});

const BlogPanel = () => {
  const classess = useStyle();
  
  return (
    <>
      <Box
        className={classess.box}
        sx={{
        
          position: "relative",
       
          mt: { lg: 10, xs: 6 },
          mb: { lg: 10, xs: 6 },
        }}
      >
        <Box
          className={classess.overlay}
          sx={{
            pb: { xs: 9, sm: 9, lg: 8, xl: 12 },
            pt: { xs: 9, sm: 9, lg: 8, xl: 12 },
          }}
        >
          <Box pl={"6.6%"} pr={"6.6%"}>
            <Typography
              sx={{ pb: { xs: 2, sm: 2, lg: 3 } }}
              className={classess.typography2}
              // vareint="h1"
            >
              what_to <br /> expect_from_us
            </Typography>
            <Typography
              sx={{ pb: { xs: 2, sm: 2, lg: 2 } }}
              className={classess.typography1}
            >
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean
              vel diam elit. Donec id lectus congue, tempor nunc sit amet,
              ullamcorper orci.
            </Typography>
            <Box className={classess.btn}>
              <Button
                variant="outlined"
                className={classess.buton}
                sx={{
                  borderRadius: "50px !important",
                  color: "black",
                  background: "white",
                  border: "1px solid gray",
                  fontWeight: "bold",
                  // width: { lg: "300px !important", xl: "220px" },
                  height: { lg: "45px" },
                }}
              >
              start_now
              </Button>
            </Box>
          </Box>
        </Box>
        {/* <Box
          component="img"
          src={rightHalf}
          sx={{
            position: "absolute",
            right: 0,
            // top: { lg: 55, md: 600, sm: 700, xs: 900 },
            top: {
              xs: "-170px",
              md: "-140px !important",
              xl: "-160px !important",
              lg: "-160px !important",
            },
          }}
          height={{ xl: 740, lg: 740, md: 650, sm: 400, xs: 350 }}
          width={{ xl: 740, lg: 740, md: 650, sm: 400, xs: 350 }}
          zIndex="-1"
        /> */}
      </Box>
    </>
  );
};

export default BlogPanel;
