import { Box, Grid } from '@mui/material';
import React from 'react'
import { blogList } from '../../api';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { Link } from 'react-router-dom';




const BlogList = () => {
const [dataslice,setData]=React.useState([])
const [load,setLoad]=React.useState(6)
    const getData = () => {
        blogList.getBlog().then((res) => {
            setData(res?.data);
            console.log(res?.data,'data')
        });
       
      };
    
      React.useEffect(() => {
        getData();
      }, []);

      const loadMore=()=>{
          setLoad(load+6)
      }
      const data= dataslice.slice(0,load);
  return (
    <>
   <Box sx={{mx:20,my:10}}>
    <Grid container spacing={0}>
    {data.map((item) => {
 return   <Grid item sx={{px:2,py:5}} key={item.id} xs={12}sm={6}md={4}lg={4}>
 
    
  <Card  sx={{ maxWidth: 400,height: 400 }}>
  <Link className='blogItem-link' to={`/blog/${item.id}`}>    
 </Link>  
      <CardMedia
        component="img"
        height="140"
        image={item.Image}
        alt="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h6" fontWeight='bold' component="div">
         {item.Title}
        </Typography>
        
        <Typography variant="body2" color="text.secondary">
         {item.Subtitle}
        </Typography>
      </CardContent>
    
    </Card>
   
  </Grid>
    })}
</Grid>
<Button onClick={loadMore} sx={{background:'black'}}>Load More</Button>
</Box>
    </>
  )
}

export default BlogList




