import { blogList } from '../../api';
import EmptyList from '../EmptyList';
const SingleBlog = () => {
    const { id } = useParams();
  const [blog, setBlog] = useState(null);
  console.log(blog);
const [data,setData] = useState([])

const getData = () => {
    blogList.getBlog().then((res) => {
        setData(res?.data);
        console.log(res.data,'data')
    });
   
  };
  useEffect(() => {
      
    let blog = data.find((blog) => blog.id == id);
