import { blogList } from '../../api';
import EmptyList from '../EmptyList';
const SingleBlog = () => {
    const { id } = useParams();
  const [blog, setBlog] = useState(null);
  console.log(blog);
const [data,setData] = useState([])


    

    const loadUser = async () => {
        const res = await axios.get(`https://61791a83aa7f3400174047a6.mockapi.io/v1/GetBLogs/${id}`);
        setBlog(res.data);
      };
   

  useEffect(() => {
      
  


    loadUser();

  
  }, []);

 
  return (
    <>
    {/* {data.map((item)=>{
        return <Typography>{item.id}</Typography>
    })} */}
    {blog ? (
<Box sx={{}}>
    <Typography variant='h5'>
    {blog.data.Title}
    </Typography>
    <Typography variant='body2'>
    {blog.data.Subtitle}
    </Typography>
    <Box component='img' src={blog.data.Image} />   
    
</Box>    
    
) : (
        <EmptyList />
      )}
    </>
  )
}

export default SingleBlog
