import React from "react";
import { Container, Box, Grid, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import CallIcon from "@mui/icons-material/Call";
import EmailIcon from "@mui/icons-material/Email";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import InstagramIcon from "@mui/icons-material/Instagram";
import TwitterIcon from "@mui/icons-material/Twitter";
import FacebookIcon from "@mui/icons-material/Facebook";
import { useNavigate } from "react-router-dom";

const useStyles = makeStyles({
  root: {
    // background: "linear-gradient(to right, #74ebb4, #3b72df)",
    background: "#222020",
    width: "100%",
  },
  title: {
    color: "white",
    fontWeight: "bold",
    fontSize: "30px !important",
    "@media (max-width: 600px)": {
      fontSize: "20px !important",
    },
  },
  listhead: {
    color: "white",
    fontWeight: "bold",
    "@media (max-width: 600px)": {
      fontSize: "10px !important",
    },
  },

  listItm: {
    color: "white",
    cursor: "pointer",
    fontSize: "14px !important",
    wordBreak: "break-all",
    "@media (max-width: 600px)": {
      fontSize: "10px !important",
    },
    "@media (max-width: 400px)": {
      fontSize: "8px !important",
    },
  },
});

function UPFooter() {
  const navigate = useNavigate();
  const classes = useStyles();
  return (
    <Box className={classes.root}>
      <Container maxWidth={"lg"}>
        <Box
          component="div"
          sx={{
            height: "1px",
            mt: { lg: 10, xs: 5, md: 8 },
            backgroundColor: "white",
            width: "100%",
          }}
        ></Box>
        <Grid container sx={{ pt: { lg: 8, xs: 4, md: 6 } }} pb={4}>
          <Grid
            item
            xs={12}
            sm={12}
            md={6}
            lg={6}
            xl={6}
            sx={{ pr: { lg: 4, xs: 1 }, pl: { lg: 4, xs: 1 } }}
          >
            <Typography className={classes.title}>Get in touch with us for your services</Typography>
            <Box display="flex" justifyContent={"flex-start"} pb={4} pr={0}>
          <Box display="flex" justifyContent={"space-between"}>
            <InstagramIcon
              sx={{ fill: "white", pl: 1, pr: 1,pt:3, fontSize: "30px" }}
            />
            <TwitterIcon
              sx={{ fill: "white", pl: 1, pr: 1,pt:3, fontSize: "30px" }}
            />
            <FacebookIcon
              sx={{ fill: "white", pl: 1, pr: 1,pt:3, fontSize: "30px" }}
            />
          </Box>
        </Box>
          </Grid>
          <Grid item xs={4} sm={4} md={2} lg={2} xl={2}>
            <Typography className={classes.listhead}>QUICK LINKS</Typography>
            <List>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Contact Us
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Ordering & Payment
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Shopping
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Returns
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  FAQ
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Sizing Guide
                </Typography>
              </ListItem>
            </List>
          </Grid>
          <Grid item xs={4} sm={4} md={2} lg={2} xl={2}>
            <Typography className={classes.listhead}>SERVICES</Typography>
            <List>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Abour Abobe XD kit
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Work With Us
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Privacy Policy
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Terms & Conditions
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Press Enquiries
                </Typography>
              </ListItem>
            </List>
          </Grid>
          <Grid item xs={4} sm={4} md={2} lg={2} xl={2}>
            <Typography className={classes.listhead}>CONTACT US</Typography>
            <List>
              <ListItem disablePadding>
                <ListItemIcon>
                  <CallIcon sx={{ fill: "white" }} />
                </ListItemIcon>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  +44 345678903
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <ListItemIcon>
                  <EmailIcon sx={{ fill: "white" }} />
                </ListItemIcon>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  MIS@gmail.com
                </Typography>
              </ListItem>
              <ListItem disablePadding>
                <ListItemIcon>
                  <LocationOnIcon sx={{ fill: "white" }} />
                </ListItemIcon>
                <Typography
                  onClick={() => navigate("/")}
                  className={classes.listItm}
                >
                  Find a store
                </Typography>
              </ListItem>
            </List>
          </Grid>
        </Grid>
       
      </Container>
    </Box>
  );
}

export default UPFooter;
