import React from "react";
import Image from "../../assets/Image.png";
import { Box, Typography, Button } from "@mui/material";
import { makeStyles } from "@mui/styles";
// import rightHalf from "../../assets/images/right-2.png";


const useStyle = makeStyles({
  btn: {
    marginTop: "3% !important",
    "@media(min-width: 1390px)": {
      marginTop: "1% !important",
    },
  },
  box: {
    width: "50%",
   
    backgroundImage: `url(${Image})`,
    backgroundSize: "cover !important",
    backgroundPosition: "center",
  },

  article: {
    height: '100%',
    position: 'relative',
    overflow: 'hidden',
},

picture: {
    objectFit: 'cover',
    width: '100%',
    height: '100%',
},

header: {
    fontSize: '80px',
    color: 'white',
    textAlign: 'center',
    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    height: 'fit-content',
    margin: 'auto',
}
});

const BlogPanel = () => {
  const classess = useStyle();
  
  return (
    <>
       <Box className={classess.article}>
      <Box component='img' className={classess.image} src={Image} alt="background" />
      <Typography sx={{ fontSize: '80px',
    color: 'white',
    textAlign: 'center',
    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    height: 'fit-content',
    margin: 'auto',}}>React Is Awesome</Typography>
    </Box>
    </>
  );
};

export default BlogPanel;
